mapper.insert:
INSERT INTO board ( board_content, user_id, uploaded_at, view
, image_id
) VALUES ( #{board_content}, #{user_id}, NOW(), 0
, #{image_id}
)

mapper.update:
UPDATE board 
SET view = view + 1 
WHERE board_id = #{board_id}

mapper.select:
SELECT user_id
FROM board
WHERE board_id = #{board_id}

mapper.update:
UPDATE board
board_content = #{board_content}, image_id = #{image_id},
WHERE board_id = #{board_id}
